var tustan_json = '{"in_name_excursion":"Скелі Тустані", "main_foto":"background: url(images/exursion/bus/tustan/tustan.jpg) no-repeat center center; background-size: cover;", "smal_foto_0":"background-image: url(images/exursion/bus/tustan/tustan.jpg);", "smal_foto_1":"background-image: url(images/exursion/bus/tustan/tustan_1.jpg);", "smal_foto_2":"background-image: url(images/exursion/bus/tustan/tustan_2.jpg);", "smal_foto_3":"background-image: url(images/exursion/bus/tustan/tustan_3.jpg);", "addition_info":"<p><b>Вартість: 300 грн./особа, діти до 13 років 250 грн./особа</b></p><p><b>Тривалість:</b> 10 годин</p><p><b>Виїзд:</b> 8:45 (при зборі групи)</p><p><b>Вартість вхідних квитків:</b></p><p><b>вхід на територію наскельної фортеці “Тустань” та до Музею історії Тустані: </b>(комплексний квиток): дорослі – 20 грн./ос., студенти/школярі/ пенсіонери – 10 грн</p><p><b>водоспад Кам’янка Національного парку “Сколівські Бескиди”:  </b>дорослі – 5 грн./ос., діти до 14 років – 3 грн./ос., діти до 7 років – б/к.</p><p><b>Є можливість пообідати на території відпочинкового комплексу.</b>", "main_description":"<p>На теренах Львівської області знаходиться одне з історичних та природних див краю – скелі Тустані. В часи середньовіччя на скелях знаходилась велика твердиня, що захищала регіон від ворогів, а також виконувала функції митниці. Під час екскурсії Ви також відвідаєте музей, що присвячений цій історичній пам’ятці.</p> <p>Після цього ви побачите карпатський водоспад на р. Кам’янці  та розташоване у горах так зване Мертве (Журавлине) озеро.</p>" }';
var castles_json = '{"in_name_excursion":"Замки Закарпаття", "main_foto":"background: url(images/exursion/bus/castles/castles.jpg) no-repeat center center; background-size: cover;", "smal_foto_0":"background-image: url(images/exursion/bus/castles/castles.jpg);", "smal_foto_1":"background-image: url(images/exursion/bus/castles/castles_1.jpg);", "smal_foto_2":"background-image: url(images/exursion/bus/castles/castles_2.jpg);", "smal_foto_3":"background-image: url(images/exursion/bus/castles/castles_3.jpg);", "addition_info":"<p><b>450 грн/особа, діти до 13 р. – 400 грн./особа</b></p><p><b>Виїзд:</b> щосуботи, 7:00, повернення до Львова о 22:00-23:00</p><p><b>Вартість вхідних квитків:</b></p><p><b>Замок Шенборнів — 10 грн</b></p><p><b>Замок Паланок — 20 грн</b></p><p><b>Ужгородський замок — 25 грн.</b></p><p><b>Обід в ресторані “Деца у нотаря” — від 100 грн.</b></p>", "main_description":"<p>На теренах мальовничого Закарпаття розкинуло безліч прекрасних замків та палаців. Від деяких залишились лише руїни, проте частина дійшла до нашого часу в чудовому стані. Під час екскурсії Ви відвідаєте три закарпатські замки: мисливський палац графів Шенборнів, один із найкрасивіших замків – середньовічний замок Паланок у Мукачеві, а також замок у столиці Закарпаття – давньому місті Ужгороді. У замках також діють експозиції з історії регіону, культури та побуту місцевого населення. Також під час екскурсії Ви матимете змогу пообідати в одному із найколоритніших ресторанів Закарпаття – “Деца у Нотаря”.</p>" }';


var seredn_json_ru = '{"in_name_excursion":"Средневековый Львов и подземелья", "main_foto":"background: url(images/exursion/lviv/seredn/seredn.jpg) no-repeat center center; background-size: cover;", "smal_foto_0":"background-image: url(images/exursion/lviv/seredn/seredn.jpg);", "smal_foto_1":"background-image: url(images/exursion/lviv/seredn/seredn_1.jpg);", "smal_foto_2":"background-image: url(images/exursion/lviv/seredn/seredn_2.jpg);", "smal_foto_3":"background-image: url(images/exursion/lviv/seredn/seredn_3.jpg);", "addition_info":"<p><b>Продолжительность экскурсии: 2,5 – 3 часа.</b><p><b>Стоимость: 80 грн. / чел.</b><p><b>Дополнительные расходы: входные билеты в музеи</b><p><b>Дополнительно можно заказать индивидуальные и групповые экскурсии</b>", "main_description":"<p>Недаром говорят, что все дороги ведут во Львов. Львов - это уникальный город с богатой историей, который помнит украинских князей, польских королей и австрийских императоров. Во Львове вы сможете увидеть четыре квартала средневекового города, где на протяжении веков жили представители разных народов: поляки и украинцы, евреи и армяне. Также познакомитесь с сердцем нашего города - старинной Площадью Рынок, где находится здание магистрата с Ратушей и дома некогда самых богатых львовян. <p>Кроме того, вы увидите старые храмы нашего города, в частности католический кафедральный собор, Армянскую церковь и православную церковь Успения Пресвятой Богородицы, а также костёлы иезуитов и доминиканцев. Вместе с тем, вы узнаете кто научил европейцев пить кофе и познакомил с мазохизмом, для чего в средневековье использовали опиум и почему почти все дома на площади Рынок имеют только три окна на фасаде<p>Дополнительно вы сможете посетить подземелья доминиканского и иезуитского монастырей, аптеку-музей и увидите, как во Львове выращивается кофе." }';
var avstriy_json_ru = '{"in_name_excursion":"Австрийский Львов", "main_foto":"background: url(images/exursion/lviv/avstriy/avstriy.jpg) no-repeat center center; background-size: cover;", "smal_foto_0":"background-image: url(images/exursion/lviv/avstriy/avstriy.jpg);", "smal_foto_1":"background-image: url(images/exursion/lviv/avstriy/avstriy_1.jpg);", "smal_foto_2":"background-image: url(images/exursion/lviv/avstriy/avstriy_2.jpg);", "smal_foto_3":"background-image: url(images/exursion/lviv/avstriy/avstriy_3.jpg)", "addition_info":"<p><b>Продолжительность экскурсии: 2,5 – 3 часа.</b><p><b>Стоимость: 80 грн. / чел.</b><p><b>Дополнительные расходы: входные билеты в музеи</b><p><b>Дополнительно можно заказать индивидуальные и групповые экскурсии</b>", "main_description":"В XVIII-XX вв. Львов пережил свой расцвет в составе мощной Австрийской империи. Этот период можно без возражений назвать Золотой эпохой в истории города. Пешая прогулка политическим, экономическим и интеллектуальным центром Львова австрийского периода перенесет Вас в эпоху XIX в., когда Львов назывался Лембергом и был столицей Королевства Галиции и Владимирии. Вы посетите Львовский национальный театр оперы и балета, узнаете об истории создания украинского Национального музея во Львове и прогуляетесь банковским кварталом города, а также сможете посетить роскошное казино. А слышали вы когда-нибудь о Львовском Монмартре, где проживала городская художественная и научная элита, раскинувшийся между двумя львовскими университетами - Львовским национальным университетом им. И. Франко (бывшее здание Галицкого парламента) и НУ \\\"Львовская Политехника\\\". Прогулку между университетами Вы совершите старейшим парком Украины, над которым возвышается архитектурная жемчужина Львова - величественный барочный собор Св. Юра. На экскурсии Вы увидите шедевр дворцовой архитектуры во Львове - дворец польских магнатов Потоцких, а также узнаете историю незаконного возведения первого львовского небоскреба." }';
var knyaz_json_ru = '{"in_name_excursion":"Княжий Львов", "main_foto":"background: url(images/exursion/lviv/knyaz/knyaz.jpg) no-repeat center center; background-size: cover;", "smal_foto_0":"background-image: url(images/exursion/lviv/knyaz/knyaz.jpg);", "smal_foto_1":"background-image: url(images/exursion/lviv/knyaz/knyaz_1.jpg);", "smal_foto_2":"background-image: url(images/exursion/lviv/knyaz/knyaz_2.jpg);", "smal_foto_3":"background-image: url(images/exursion/lviv/knyaz/knyaz_3.jpg);", "addition_info":"<p><b>Продолжительность экскурсии: 2 часа.</b><p><b>Стоимость: 70 грн. / чел.</b><p><b>Дополнительные расходы: входные билеты в музеи</b><p><b>Дополнительно можно заказать индивидуальные и групповые экскурсии</b>", "main_description":"Княжий Львов находится недалеко от современного центра города - вокруг древней площади Старый Рынок. Именно оттуда начинается история города, который еще в далеком XIII в. основал король Данила Галицкий для своего сына Льва. Во время экскурсии Вы прогуляетесь старыми улочками города, узнаете древнейшую историю города, посетите первые христианские святыни Львова - княжескую церковь Св. Николая и костел Иоанна Крестителя, который князь Лев подарил любимой жене Констанции, ознакомитесь с пластической панорамой города, которая экспонируется в этом храме. Кроме того Вы посетите один из старейших монастырей во Львове - Св. Онуфрия, где проживал украинский первопечатник Иван Федорович. Кроме мужского монастыря Вы также увидите первый женский монастырь во Львове - монастырь бенедиктинок, монахини которого варили пиво в нашем городе. Рядом находится храм Марии Снежной, который был главным католическим храмом Львова в XIII-XIV вв." }';
var seven_json_ru = '{"in_name_excursion":"Семь чудес Львова", "main_foto":"background: url(images/exursion/lviv/seven/seven.jpg) no-repeat center center; background-size: cover;", "smal_foto_0":"background-image: url(images/exursion/lviv/seven/seven.jpg);", "smal_foto_1":"background-image: url(images/exursion/lviv/seven/seven_1.jpg);", "smal_foto_2":"background-image: url(images/exursion/lviv/seven/seven_2.jpg);", "smal_foto_3":"background-image: url(images/exursion/lviv/seven/seven_3.jpg);", "addition_info":"<p><b>Продолжительность экскурсии: 2,5 – 3 часа.</b></p> <p><b>Стоимость: 80 грн. / чел.</b><p><b>Дополнительные расходы: входные билеты в музеи</b><p><b>Дополнительно можно заказать индивидуальные и групповые экскурсии</b>", "main_description":"Львов имеет собственные Семь Чудес - невероятно красивых памятников архитектуры XVII-XX вв. Эти сооружения являются украшением города уже несколько веков. Во время экскурсии Вы познакомитесь с историей таких выдающихся памятников архитектуры как кафедральный собор Св. Юра, что является одним из важнейших религиозных центров Львова, и посетите ренессансный комплекс православной Успенской церкви - единственной православной церкви в центре древнего города, а также величественный Бернардинский монастырь вместе с церковью Св. Андрея. Увидите уникальную часовня семьи львовских патрициев Боимов, а также три жемчужины австрийского Львова - величественный дворец Потоцких, неповторимый Оперный театр и роскошное казино. Также сможете насладились красотой внутреннего убранства этих величественных памятников архитектуры." }';
var vechir_json_ru = '{"in_name_excursion":"Вечерний Львов", "main_foto":"background: url(images/exursion/lviv/vechir/vechir.jpg) no-repeat center center; background-size: cover;", "smal_foto_0":"background-image: url(images/exursion/lviv/vechir/vechir.jpg);", "smal_foto_1":"background-image: url(images/exursion/lviv/vechir/vechir_1.jpg);", "smal_foto_2":"background-image: url(images/exursion/lviv/vechir/vechir_2.jpg);", "smal_foto_3":"background-image: url(images/exursion/lviv/vechir/vechir_3.jpg);", "addition_info":"<p><b>Продолжительность экскурсии: 2,5 – 3 часа.</b></p><p><b>Стоимость: 250 грн. </b><p><b>Дополнительные расходы: входные билеты в музеи</b><p><b>Дополнительно можно заказать индивидуальные и групповые экскурсии</b>", "main_description":"После заката Львов меняет свой облик. Походите по узким улочкам древнего города, послушайте истории об его жителях, их победах и поражениях, и рассмотрите вечерний Львов с самой высокой башни города - Львовской Ратуши. Во время экскурсии Вы услышите интересные истории о польских королях и русских царях, которые посещали наш город, узнаете о шейха Ирана, который останавливался в одном из самых роскошных отелей Львова, в котором также жили известные французские писатели Оноре де Бальзак и Жан-Поль Сартр. Львовяне также умели развлекаться, а самыми популярными местами для развлечений были львовские кофейни и казино, в которых кипела ночная жизнь Львова." }';
var sacr_json_ru = '{"in_name_excursion":"Сакральные памятники Львова", "main_foto":"background: url(images/exursion/lviv/sacr/sacr.jpg) no-repeat center center; background-size: cover;", "smal_foto_0":"background-image: url(images/exursion/lviv/sacr/sacr.jpg);", "smal_foto_1":"background-image: url(images/exursion/lviv/sacr/sacr_1.jpg);", "smal_foto_2":"background-image: url(images/exursion/lviv/sacr/sacr_2.jpg);", "smal_foto_3":"background-image: url(images/exursion/lviv/sacr/sacr_3.jpg);", "addition_info":"<p><b>Продолжительность экскурсии: 2,5 – 3 часа.</b></p><p><b>Стоимость: 80 грн. / чел.</b><p><b>Дополнительные расходы: входные билеты в музеи</b><p><b>Дополнительно можно заказать индивидуальные и групповые экскурсии</b>", "main_description":"Львов - один из духовных центров Украины. В городе Льва находится более полутора сотни действующих храмов различных конфессий. Во время экскурсии Вы погрузитесь в духовную жизнь, посетите самые известные храмы львовского центра города - католический кафедральный собор, ренессансную святыню Львова - православную Успенскую церковь, собор Армянской апостольской церкви. Также побываете в барочном доминиканском костёле, и посетите самый большой храм древнего Львова - иезуитский костёл апостолов Петра и Павла. На экскурсии вы узнаете об истории чудотворных икон и жизни христианских святых, мощи которых хранятся в львовских храмах. <p><b>Продолжительность экскурсии: 2,5 – 3 часа." }';
var romantic_json_ru = '{"in_name_excursion":"Львов - город влюбленных", "main_foto":"background: url(images/exursion/lviv/romantic/romantic.jpg) no-repeat center center; background-size: cover;", "smal_foto_0":"background-image: url(images/exursion/lviv/romantic/romantic.jpg);", "smal_foto_1":"background-image: url(images/exursion/lviv/romantic/romantic_1.jpg);", "smal_foto_2":"background-image: url(images/exursion/lviv/romantic/romantic_2.jpg);", "smal_foto_3":"background-image: url(images/exursion/lviv/romantic/romantic_3.jpg);", "addition_info":"<p><b>Продолжительность экскурсии: 2,5 часа.</b></p><p><b>Стоимость: 300 грн. за экскурсию</b><p><b>Дополнительные расходы: входные билеты в музеи</b><p><b>Дополнительно можно заказать индивидуальные и групповые экскурсии</b>", "main_description":"Во Львове тоже есть свои истории любви - счастливой и трагической. Наш город богат на многочисленные реальные и легендарные истории любви, наравне с Ромео и Джульеттой достойные на создание шедевров литературы и кино. Узнайте о любви во Львове во время романтической экскурсии улочками древнего города. В частности о любви польских королей Владислава IV и Яна III Собеского, о романтично-трагической любви художника Артура Гротгера и его невесты Ванды Моне. В свое время во Львове находился известный на весь мир венецианец Джакомо Казанова, который прославился своим неудержимым влечением к жизни. Городская аристократия оставила после себя множество скандальных историй, вроде истории любви жены графа Скарбека и Александра Фредро. И куда же без любовных треугольников? К сожалению, некоторые из них имели летальный исход для некоторых участников." }';
var lychakiv_json_ru = '{"in_name_excursion":"Лычаковское кладбище", "main_foto":"background: url(images/exursion/lviv/lychakiv/lychakiv.jpg) no-repeat center center; background-size: cover;", "smal_foto_0":"background-image: url(images/exursion/lviv/lychakiv/lychakiv.jpg);", "smal_foto_1":"background-image: url(images/exursion/lviv/lychakiv/lychakiv_1.jpg);", "smal_foto_2":"background-image: url(images/exursion/lviv/lychakiv/lychakiv_2.jpg);", "smal_foto_3":"background-image: url(images/exursion/lviv/lychakiv/lychakiv_3.jpg);", "addition_info":"<p><b>Продолжительность экскурсии: 2 часа.</b></p><p><b>Стоимость: 80 грн. / чел.</b><p><b>Дополнительные расходы: входной билет</b><p><b>Дополнительно можно заказать индивидуальные и групповые экскурсии</b>", "main_description":"Во Львове находится одно из красивейших кладбищ Европы - Лычаковский некрополь. Раскинувшись на восток от центра города, он уже более двухсот лет очаровывает львовян и гостей города своей красотой, а люди, которые нашли последний покой на Лычакове прославили город на весь мир. Среди них: украинские писатели Маркиян Шашкевич и Иван Франко, непревзойденная оперная певица Соломия Крушельницкая, известные львовские архитекторы Зигмунт Горголевский и Юлиан Захариевич, математик мирового уровня Стефан Банах. Также на кладбище находятся два величественных военные мемориалы воинам Украинской галицкой армии и польским орлятам." }';
var opera_json_ru = '{"in_name_excursion":"Львовская опера", "main_foto":"background: url(images/exursion/lviv/opera/opera.jpg) no-repeat center center; background-size: cover;", "smal_foto_0":"background-image: url(images/exursion/lviv/opera/opera.jpg);", "smal_foto_1":"background-image: url(images/exursion/lviv/opera/opera_1.jpg);", "smal_foto_2":"background-image: url(images/exursion/lviv/opera/opera_2.jpg);", "smal_foto_3":"background-image: url(images/exursion/lviv/opera/opera_3.jpg);", "addition_info":"<p><b>Продолжительность экскурсии: 1 часа.</b></p><p><b>Стоимость: 150 грн. </b><p><b>Дополнительные расходы: входной билет</b><p><b>Дополнительно можно заказать индивидуальные и групповые экскурсии</b>", "main_description":"Львовская Опера - одна из самых красивых в Европе. Возведена в начале ХХ в. как доминантное здание на центральной улице города, Опера уже более ста лет очаровывает львовян и гостей города своей красотой. Не уступает красотой и интерьер театра, в частности величественное фойе, зеркальный зал и зал зрителей. Интересна и история жизни и творчества самой Соломии Крушельницкой, всемирно оперной певицы. Во время экскурсии Львовским национальным академическим театром оперы и балета имени Соломии Крушельницкой Вы познакомитесь с историей театрального искусства во Львове, экстерьером и интерьером Оперы, а также узнаете о выдающихся деятелях искусства, которые выступали на сцене Львовского оперного театра, среди которых немало известных европейских оперных исполнителей." }';
var kazyno_json_ru = '{"in_name_excursion":"Дворянское казино", "main_foto":"background: url(images/exursion/lviv/kazyno/kazyno.jpg) no-repeat center center; background-size: cover;", "smal_foto_0":"background-image: url(images/exursion/lviv/kazyno/kazyno.jpg);", "smal_foto_1":"background-image: url(images/exursion/lviv/kazyno/kazyno_1.jpg);", "smal_foto_2":"background-image: url(images/exursion/lviv/kazyno/kazyno_2.jpg);", "smal_foto_3":"background-image: url(images/exursion/lviv/kazyno/kazyno_3.jpg);", "addition_info":" <p><b>Продолжительность экскурсии: 1 часа.</b></p><p><b>Стоимость: 150 грн. </b><p><b>Дополнительные расходы: входной билет</b><p><b>Дополнительно можно заказать индивидуальные и групповые экскурсии</b>", "main_description":"Дворянское казино во Львове поражает своей красотой. Здание, построенное в конце XIX в. для городской элиты, была местом, где проводились изысканные балы и рауты. Внутренняя отделка сих пор поражает своим великолепием. Во время экскурсии зданием казино вы увидите, где проводили свое свободное время львовские богачи конца XIX - нач. ХХ в., А еще предлагаем вам узнать как в те времена проходили вечеринки городской элиты. Этот памятник архитектуры также известен тем, что режиссеры и продюсеры из разных стран снимали свои фильмы среди австрийского блеска и роскоши. Именно здесь проходили съемки известной киноленты «Три мушкетера», где Львов был Парижем, а интерьеры дворянского казино использовались для дворца кардинала Ришелье." }';
var netypova_json_ru = '{"in_name_excursion":"Нетипичная", "main_foto":"background: url(images/exursion/lviv/netypova/netypova.jpg) no-repeat center center; background-size: cover;", "smal_foto_0":"background-image: url(images/exursion/lviv/netypova/netypova.jpg);", "smal_foto_1":"background-image: url(images/exursion/lviv/netypova/netypova_1.jpg);", "smal_foto_2":"background-image: url(images/exursion/lviv/netypova/netypova_2.jpg);", "smal_foto_3":"background-image: url(images/exursion/lviv/netypova/netypova_3.jpg);", "addition_info":"<p><b>Продолжительность экскурсии: 1 часа.</b></p><p><b>Стоимость: 100 грн. / чел.</b><p><b>Дополнительные расходы: входные билеты в музеи</b><p><b>Дополнительно можно заказать индивидуальные и групповые экскурсии</b>", "main_description":"Бесспорно, сердцем города Львова является Площадь Рынок и улицы вокруг нее, но во Львове можно увидеть еще много интересного. В XIX-XX вв. жизнь бурлила и в других участках города. Приглашаем вас на экскурсию улочками Львова, где вы увидите храм Св. Георгия, построенный в неовизантийском стиле в начале ХХ в., совершите путешествие по проспекту Шевченко (когда-то улица Академическая), на котором кипела университетскую жизнь Львова в конце XIX - начале ХХ в. В одном из кафе на этом проспекте встречались ведущие европейские математики пер. пол. ХХ в. - Стефан Банах и Станислав Улям. Увидите самую старую университетскую библиотеку Украины. Узнаете о таких колоритных фигур как профессор Львовского университета Войцех Дидушицкий, \\\"маму\\\" Теличкову и \\\"человека-муху\\\"." }';
